import React, { useState } from 'react';
import MovieList from './components/MovieList';
import Filter from './components/Filter';
import AddMovie from './components/AddMovie';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const initialMovies = [
  { id: 1, title: 'Harry Potter', description: 'A wizard boy...', posterUrl: 'data:image/jpeg;base64,', rating: 9 },
  { id: 2, title: 'The Lord of the Rings', description: 'An epic fantasy...', posterUrl: 'https://m.media-amazon.com/images/M/MV5BNzA5ZDNlZWMtM2NhNS00NDJjLTk4NDItYTRmY2EwMWZlMTY3XkEyXkFqcGdeQXVyNzkwMjQ5NzM@._V1_.jpg', rating: 9 },
  { id: 3, title: 'The Chronicles of Narnia', description: 'A magical world...', posterUrl: 'https://c8.alamy.com/compfr/2e52bam/la-projection-du-film-les-chroniques-de-narnia-premiere-du-prince-caspian-a-disneyland-paris-a-marne-la-vallee-france-le-20-juin-2008-photo-de-giancarlo-gorassini-abacapress-com-2e52bam.jpg', rating: 8 },
  { id: 4, title: 'Toy Story', description: 'A story of toys...', posterUrl: 'https://m.media-amazon.com/images/M/MV5BYWE0MzUzNDctNTFiYS00YzY3LWEzYWMtMDQyMWE5OTMxOTBiXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_.jpg', rating: 8 },
  { id: 5, title: 'Finding Nemo', description: 'A fish adventure...', posterUrl: 'https://cdn-europe1.lanmedia.fr/var/europe1/storage/images/europe1/culture/trois-raisons-qui-prouvent-que-toy-story-a-revolutionne-le-film-danimation-3471521/44791538-1-fre-FR/Trois-raisons-qui-prouvent-que-Toy-Story-a-revolutionne-le-film-d-animation.jpg', rating: 8 },
  { id: 6, title: 'The Lion King', description: 'A lion cub...', posterUrl: 'data:image/jpeg;base64,', rating: 9 }
];

const App = () => {
  const [movies, setMovies] = useState(initialMovies);
  const [filteredMovies, setFilteredMovies] = useState(initialMovies);

  const handleFilter = ({ title, rating }) => {
    let filtered = movies;
    if (title) {
      filtered = filtered.filter(movie => movie.title.toLowerCase().includes(title.toLowerCase()));
    }
    if (rating) {
      filtered = filtered.filter(movie => movie.rating >= rating);
    }
    setFilteredMovies(filtered);
  };

  const handleAddMovie = (newMovie) => {
    setMovies([...movies, newMovie]);
    setFilteredMovies([...movies, newMovie]);
  };

  return (
    <div className="container" style={{ backgroundColor: '#2c3e50', minHeight: '100vh', color: '#fff' }}>
      <h1 className="text-center my-4">Lifestream Movie App</h1>
      <AddMovie onAdd={handleAddMovie} />
      <Filter onFilter={handleFilter} />
      <MovieList movies={filteredMovies} />
    </div>
  );
};

export default App;
